INSERT INTO `admin_action_log` VALUES (1,2,8,'approve','Review is fair and constructive.','2025-07-07 18:22:33'),(1,10,3,'delete','Review lacked meaningful content.','2025-07-07 18:22:33'),(3,5,6,'approve','No issues found — approved.','2025-07-07 18:22:33'),(3,6,1,'delete','Removed due to spam and trolling.','2025-07-07 18:22:33'),(4,22,4,'flag','Marked as low effort for admin review.','2025-07-07 18:22:33'),(5,15,5,'flag','Contains potentially offensive language.','2025-07-07 18:22:33'),(7,3,2,'flag','Flagged for offensive language.','2025-07-07 18:22:33'),(7,21,9,'flag','Marked for moderator second look.','2025-07-07 18:22:33'),(9,18,7,'delete','Spammy and toxic language present.','2025-07-07 18:22:33'),(10,9,10,'approve','Genuine opinion, left as-is.','2025-07-07 18:22:33');
